SELECT
COUNT(hospital_records.id)
FROM
hospital_records
LEFT JOIN patient ON patient.id = hospital_records.patientId
WHERE hospital_records.deleted = 0

    AND hospital_records.patientId = #{search.patientId}


    AND patient.userName = #{search.userName}


    AND patient.sex = #{search.sex}


    AND patient.nation = #{search.nation}


    AND hospital_records.hospitalizationTime > #{search.startHospitalizationTime}
    AND #{search.endHospitalizationTime}> hospital_records.hospitalizationTime


    AND hospital_records.dischargeTime > #{search.startDischargeTime}
    AND #{search.endDischargeTime} > hospital_records.dischargeTime


    AND hospital_records.`status` = #{search.status}


    AND hospital_records.id IN

        #{item}

SELECT hospital_records.id, hospital_records.patientId, hospital_records.onsetTime, hospital_records.toHospitalTime, hospital_records.hospitalizationTime, hospital_records.description, hospital_records.dischargeTime, hospital_records.`status`, hospital_records.createTime, hospital_records.updateTime, hospital_records.deleted, patient.userName, patient.sex, patient.IDCard, patient.age, patient.phoneNumber, patient.nation, patient.livingStatus, patient.maritalStatus, patient.educationDegree, patient.medicalInsuranceType, patient.job, patient.income, patient.city, patient.address FROM hospital_records LEFT JOIN patient ON patient.id = hospital_records.patientId WHERE hospital_records.deleted = 0
AND hospital_records.patientId = #{search.patientId}
AND patient.userName = #{search.userName}
AND patient.sex = #{search.sex}
AND patient.nation = #{search.nation}
AND hospital_records.hospitalizationTime > #{search.startHospitalizationTime} AND #{search.endHospitalizationTime} > hospital_records.hospitalizationTime
AND hospital_records.dischargeTime > #{search.startDischargeTime} AND #{search.endDischargeTime} > hospital_records.dischargeTime
AND hospital_records.`status` = #{search.status}
AND hospital_records.id IN
#{item}
ORDER BY hospital_records.createTime LIMIT #{index}, #{size}

SELECT hospitalRecordsId
FROM
hospital_diagnostic_records
WHERE `type` = #{type}

    OR cerebralInfarction = #{cerebralInfarction}



        OR cerebralInfarctionTOAST like CONCAT('%', #{item}, '%')



    OR cerebralHaemorrhage = #{cerebralHaemorrhage}


    OR vascularCognitiveImpairment = #{vascularCognitiveImpairment}


    OR cerebralCerebrovascularDisease = #{cerebralCerebrovascularDisease}


    OR intracranialArteryStenosis = #{intracranialArteryStenosis}


    OR cervicalArteryStenosis = #{cervicalArteryStenosis}


    OR vertebralArteryStenosis = #{vertebralArteryStenosis}


    OR coronaryHeartDisease = #{coronaryHeartDisease}


    OR atrialFibrillation = #{atrialFibrillation}


    OR hypertension = #{hypertension}


    OR diabetes = #{diabetes}


    OR hyperlipidemia = #{hyperlipidemia}


    OR homocysteineemia = #{homocysteineemia}


    OR hyperuricAcidemia = #{hyperuricAcidemia}


    OR chronicObstructivePulmonaryDisease = #{chronicObstructivePulmonaryDisease}


    OR progressiveStroke = #{progressiveStroke}


    OR cobwebSubcavityBleeding = #{cobwebSubcavityBleeding}


    OR angina = #{angina}


    OR myocardialInfarction = #{myocardialInfarction}


    OR lowerLimbArtery = #{lowerLimbArtery}


    OR lowerLimbVeins = #{lowerLimbVeins}


    OR lungInfections = #{lungInfections}


    OR urinaryTractInfection = #{urinaryTractInfection}


    OR stressUlcers = #{stressUlcers}

SELECT hospitalRecordsId
FROM hospital_treatment_records


        OR walk = #{walk}


        OR thrombosisPrevention = #{thrombosisPrevention}



            OR thrombosisPreventionTypes like CONCAT('%', #{item}, '%')



        OR antiplatelets = #{antiplatelets}



            OR antiplateletsDrugs like CONCAT('%', #{item}, '%')



        OR anticoagulant = #{anticoagulant}



            OR anticoagulantDrugs like CONCAT('%', #{item}, '%')



        OR lipid = #{lipid}


        OR lipidStrengthen = #{lipidStrengthen}


        OR gastricTube = #{gastricTube}


        OR ureter = #{ureter}

